DROP TABLE IF EXISTS `links`;
CREATE TABLE `links` (
  `link_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `link_name` varchar(255) COLLATE utf8_unicode_ci DEFAULT '' COMMENT '//名称',
  `link_title` varchar(255) COLLATE utf8_unicode_ci DEFAULT '' COMMENT '//标题',
  `link_url` varchar(255) COLLATE utf8_unicode_ci DEFAULT '' COMMENT '//链接',
  `link_order` int(11) DEFAULT '0' COMMENT '//排序',
  `status` tinyint(1) DEFAULT '0' COMMENT '0正常/1已删除',
  `add_time` int(11) DEFAULT NULL COMMENT '添加时间',
  PRIMARY KEY (`link_id`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

