-- ----------------------------
-- Table structure for `user`
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(20) NOT NULL COMMENT '用户名',
  `user_pwd` varchar(50) NOT NULL COMMENT '密码',
  `status` tinyint(1) DEFAULT '0' COMMENT '0正常/1已删除',
  `user_phone` char(11) DEFAULT NULL COMMENT '手机号',
  `user_email` varchar(50) DEFAULT NULL COMMENT '邮箱',
  `reg_time` int(11) DEFAULT NULL COMMENT '注册时间',
  `publish` tinyint(1) DEFAULT '1' COMMENT '0已审核/1未审核',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

